import 'package:flutter/material.dart';

class CategoriaScreen extends StatelessWidget {
  final String categoriaNome;

  CategoriaScreen(this.categoriaNome);

  @override
  Widget build(BuildContext context) {
    // Produtos fictícios para cada categoria
    final Map<String, List<Map<String, String>>> produtosPorCategoria = {
      'Brinquedos': [
        {
          'nome': 'Boneco de Pelúcia',
          'descricao': 'Pelúcia macia para seu pet.',
          'imagem': 'data:image/jpeg;base64,'
        },
        {
          'nome': 'Bola de Borracha',
          'descricao': 'Bola resistente para brincar.',
          'imagem': 'data:image/jpeg;base64,'
        },
      ],
      'Rações': [
        {
          'nome': 'Ração para Cães',
          'descricao': 'Ração completa para cães.',
          'imagem': 'data:image/jpeg;base64,'
        },
        {
          'nome': 'Ração para Gatos',
          'descricao': 'Ração balanceada para gatos.',
          'imagem': 'data:image/jpeg;base64,'
        },
      ],
      'Produtos Higienicos': [
        {
          'nome': 'Shampoo Pet',
          'descricao': 'Shampoo suave para banho.',
          'imagem': 'https://images.tcdn.com.br/img/img_prod/1120656/shampoo_neutro_pet_clean_700_ml_461_1_7efc257a71ee9dc69e1bbe54cfe6d986.png',
        },
        {
          'nome': 'Lenços Umedecidos',
          'descricao': 'Lenços para higiene do seu pet.',
          'imagem':'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS8tO1tvGLrdHwanfPX1On6kxlpsaaonxOkQQ&s',

        },
      ],

    };

    final produtos = produtosPorCategoria[categoriaNome] ?? [];

    return Scaffold(
      appBar: AppBar(
        title: Text('Produtos - $categoriaNome'),
        backgroundColor: Colors.blueAccent,
      ),
      body: produtos.isNotEmpty
          ? GridView.builder(
              padding: EdgeInsets.all(10),
              gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
                crossAxisCount: 2,
                childAspectRatio: 0.75, // Proporção do cartão
                crossAxisSpacing: 10,
                mainAxisSpacing: 10,
              ),
              itemCount: produtos.length,
              itemBuilder: (context, index) {
                return Card(
                  elevation: 4,
                  child: Column(
                    crossAxisAlignment: CrossAxisAlignment.stretch,
                    children: [
                      Image.network(
                        produtos[index]['imagem']!,
                        height: 120,
                        fit: BoxFit.cover,
                      ),
                      Padding(
                        padding: const EdgeInsets.all(8.0),
                        child: Text(
                          produtos[index]['nome']!,
                          style: TextStyle(fontWeight: FontWeight.bold, fontSize: 16),
                          textAlign: TextAlign.center,
                        ),
                      ),
                      Padding(
                        padding: const EdgeInsets.symmetric(horizontal: 8.0),
                        child: Text(
                          produtos[index]['descricao']!,
                          textAlign: TextAlign.center,
                        ),
                      ),
                      Padding(
                        padding: const EdgeInsets.all(8.0),
                        child: ElevatedButton(
                          onPressed: () {
                            // Aqui você pode adicionar a lógica para adicionar ao carrinho ou detalhes do produto
                            showDialog(
                              context: context,
                              builder: (context) => AlertDialog(
                                title: Text('Adicionar ao Carrinho'),
                                content: Text('Você adicionou ${produtos[index]['nome']} ao carrinho.'),
                                actions: [
                                  TextButton(
                                    onPressed: () => Navigator.of(context).pop(),
                                    child: Text('OK'),
                                  ),
                                ],
                              ),
                            );
                          },
                          child: Text('Adicionar'),
                        ),
                      ),
                    ],
                  ),
                );
              },
            )
          : Center(
              child: Text(
                'Nenhum produto encontrado nesta categoria.',
                style: TextStyle(fontSize: 20),
              ),
            ),
    );
  }
}
